import React, { Component } from 'react';
import ItemHotProduct from './ItemHotProduct';
import axios from 'axios'

class HotProduct extends Component {

    constructor(props){
        super(props);
        this.state = {
            products : []
        }
    }

    componentWillMount(){
        axios({
            method: 'get',
            url : 'http://5c9c493b5ee0830014b7195e.mockapi.io/products',
            data : null
        }).then( res => {
            this.setState({
                products : res.data
            })
        }).catch( err => {
            console.log(err)
        })
    }

    render() {
        var products = this.state.products

        var showProduct = products.map((product, index) => {
            return <ItemHotProduct key={index} product={product} />
        })

        for (let i = 0; i < 4; i++) {
            return <ItemHotProduct key={i} product={products[i]} />            
        }

        return (
            <div className="hotproduct" id="hotproduct">
                <div className="container">
                    <div className="hotproduct__title">
                        <h1 className="title">Sản phẩm bán chạy</h1><img src="image/title_dark.png" alt="title_dark" />
                    </div>
                    <div className="row">
                        {showProduct}
                    </div>
                </div>
            </div>
        );
    }
}

export default HotProduct;
