import React, { Component } from 'react';
import './App.css';
import TaskForm from './components/TaskForm';
import Control from './components/Control';
import TaskList from './components/TaskList';
var randomstring = require("randomstring");

class App extends Component {

    constructor(props){
        super(props);
        this.state={
            tasks: [],   // id: unique, name, status
            isDisplayForm : false
        }
    }

    componentWillMount(){
        if(localStorage && localStorage.getItem('tasks')){
            var tasks = JSON.parse(localStorage.getItem('tasks'))
            this.setState({
                tasks : tasks
            })
        }
    }

    onToggleForm = () =>{
        this.setState({
            isDisplayForm : !this.state.isDisplayForm
        })
    }

    onCloseForm = () =>{
        this.setState({
            isDisplayForm : false
        })
    }
    onSubmit = (data) => {
        var { tasks } = this.state
        data.id = randomstring.generate()
        tasks.push(data)
        this.setState({
            tasks : tasks
        })
        localStorage.setItem('tasks', JSON.stringify(tasks))
    }

    onUpdateStatus = (id) => {
        var { tasks } = this.state
        var index = this.findIndex(id)
        if(index !== -1){
            tasks[index].status = !tasks[index].status
            this.setState({
                tasks : tasks
            })
        }
        localStorage.setItem('tasks', JSON.stringify(tasks))
    }

    findIndex = (id) => {
        var { tasks } = this.state
        var result = -1
        tasks.forEach((task, index) => {
            if(task.id === id) result = index
        })
        return result
    }

    onDelete = (id) => {
        console.log(id)
        var { tasks } = this.state
        tasks.forEach((task, index) => {
            if(task.id === id){
                var i = tasks.indexOf(task)
                tasks.splice(i, 1)
                alert("Bạn đã xóa " + task.name + " ra khỏi danh sách")
            }
        })
        localStorage.setItem('tasks', JSON.stringify(tasks))
    }

    render() {
        var {tasks, isDisplayForm} = this.state    // var tasks = this.state.tasks
        var elmTaskForm = isDisplayForm ? <TaskForm onSubmit={this.onSubmit} onCloseForm={this.onCloseForm} /> : ''
        return ( 
            <div className="container">                
                <div className="page-header">
                  <h1>To-Do List<small> ReactJS</small></h1>
                </div>                
                <div className="row">
                    {/* Form */}                    
                    <div className="col-xs-4 col-sm-4 col-md-4 col-lg-4">
                        {elmTaskForm}
                    </div>
                    {/* End: Form */}

                    <div className={isDisplayForm ? "col-xs-8 col-sm-8 col-md-8 col-lg-8" : "col-xs-12 col-sm-12 col-md-12 col-lg-12"}>
                        {/* Add To Do */}
                        <button 
                            type="button" 
                            className="btn btn-primary"
                            onClick={this.onToggleForm}
                        >
                            <span className="fa fa-plus mr-5"></span> Thêm công việc
                        </button>&nbsp;
                        {/* Search -Sort */}
                        <Control />   
                        {/* List */}
                        <div className="row mt-5">                            
                            <div className="col-xs-12 col-sm-12 col-md-12 col-lg-12">                       
                                <TaskList 
                                    tasks={tasks} 
                                    onUpdateStatus={this.onUpdateStatus}
                                    onDelete={this.onDelete}
                                />
                            </div>                            
                        </div>
                    </div>                    
                </div>
            </div>
        );
    }
}

export default App;
