import React, { Component } from 'react';
import ItemHotProduct from './ItemHotProduct';
import axios from 'axios'

class NewProduct extends Component {

    constructor(props){
        super(props);
        this.state = {
            products : []
        }
    }

    componentWillMount(){
        axios({
            method: 'get',
            url : 'http://localhost:3000/hot_products',
            data : null
        }).then( res => {
            this.setState({
                products : res.data
            })
        }).catch( err => {
            console.log(err)
        })
    }

    render() {
        var products = this.state.products

        var showProduct = products.map((product, index) => {
            return <ItemHotProduct key={index} product={product} />
        })
        return (
            <div className="newproduct" id="newproduct">
                <div className="container">
                    <div className="newproduct__title">
                        <h1 className="title">Sản phẩm mới</h1><img src="./../image/title_dark.png" alt="title_dark" />
                    </div>
                    <div className="row">
                    </div>
                </div>
            </div>
        );
    }
}

export default NewProduct;
