import React, { Component } from 'react';
import axios from 'axios'

class Card extends Component {

    constructor(props){
        super(props);
        this.state = {
            products : []
        }
    }

    componentWillMount(){
        axios({
            method: 'get',
            url : 'http://5c9c493b5ee0830014b7195e.mockapi.io/products',
            data : null
        }).then( res => {
            this.setState({
                products : res.data
            })
            console.log(res.data)
        }).catch( err => {
            console.log(err)
        })
    }

    render() {



        return (
            <div className="col-lg-3">
                <div className="newproduct__card">
                    <div className="newproduct__card__img">
                        <img src={this.state.products[0].urlImage} alt="ruou vang da lat" />
                    </div>
                    <div className="newproduct__card__tag--sale">
                        <div className="newproduct__card__tag--span"><span>Sale</span></div>
                    </div>
                    <div className="newproduct__card__vote">
                        <div className="row">
                            <div className="col-lg-6"><i className="fas fa-heart">   yêu thích</i></div>
                            <div className="col-lg-6"><i className="far fa-chart-bar">   so sánh</i></div>
                        </div>
                    </div>
                    <div className="newproduct__card__infor">
                        <h3>{this.state.products[0].name}</h3>
                        <h5>{this.state.products[0].price}<sup>đ
                        <del>- 450.000<sup>đ</sup></del></sup></h5>
                        <button className="button">add to cart</button>
                    </div>
                </div>
            </div>
        );
    }
}

export default Card;
